v = ""
w = ""
n = len(v)
m = len(w)
match_score = 1
mismatch_penalty = -1
indel_penalty = -1
dp = [[0]*(n+1) for _ in range(m+1)] #creates a dp (m+1)×(n+1) matrix to store scores 
backtrack = [[None]*(n+1) for _ in range(m+1)] #creates matrix to store directions that led to max score
for j in range(n+1): #first row initialized at 0 (alignemnet of w can start at any v position with no penalty)
    dp[0][j] = 0 
for i in range(1, m+1): #first column initialized
    dp[i][0] = dp[i-1][0] + indel_penalty #adds penalty for gaps needed to align the start of w and v
for i in range(1, m+1):
    for j in range(1, n+1): #iterate through matrixes, filling the cells
        if w[i-1] == v[j-1]:
            score_diag = dp[i-1][j-1] + match_score #if characters match, diag score = score of prev diag + match_score
        else:
            score_diag = dp[i-1][j-1] + mismatch_penalty #if mismatch, diag score = score of prev diag + mismatch_penalty
        score_up = dp[i-1][j] + indel_penalty #score for aligning w[i-1] with a gap in v
        score_left = dp[i][j-1] + indel_penalty #score for aligning v[j-1] with a gap in w
        dp[i][j] = max(score_diag, score_up, score_left) #saves max of the three possible scores
    #Checks for the max score and sets the corresponding cell in the backtrack to their corresponding directions to get the optimal path
        if dp[i][j] == score_diag:
            backtrack[i][j] = "diag"
        elif dp[i][j] == score_up:
            backtrack[i][j] = "up"
        else:
            backtrack[i][j] = "left"
max_score = float('-inf')
max_j = 0 #initialize variables to find the highest score in the last row of dp matrix
for j in range(n+1): #iterates through the last row of dp to find max score and column index
    if dp[m][j] > max_score:
        max_score = dp[m][j]
        max_j = j #end of best fitting alignment in v (longer seq)
i = m
j = max_j #backtracking starts frowm cell with tehe highest score in the last row 
v_aligned = "" #store aligned sequences
w_aligned = "" #store aligned sequences
while i > 0: #goes from highest score cell to first row (i=0)
    if backtrack[i][j] == "diag": #match/mismatch
        v_aligned = v[j-1] + v_aligned #corresponding character added to aligned strings
        w_aligned = w[i-1] + w_aligned #corresponding character added to aligned strings
        i -= 1
        j -= 1
    elif backtrack[i][j] == "up": #w[1-j] aligned with gap in v,i
        v_aligned = "-" + v_aligned
        w_aligned = w[i-1] + w_aligned
        i -= 1
    else:  # "left"
        v_aligned = v[j-1] + v_aligned #v[j-1] aligned with gap in w,j
        w_aligned = "-" + w_aligned
        j -= 1
print("Maximum fitting alignment score:", max_score)
print("Fitting alignment:")
print(v_aligned)
print(w_aligned)
